import React, { useContext, useState } from 'react';
import './Css/TelaStyle.css';
import '../Components/CSS/HeaderStyle.css'
import { Button, Checkbox, Box } from '@material-ui/core';
import TelaDois from './TelaDois';
import EmailTel from '../EmailTel';
import Birthday from '../Birthday';
import ValidacoesCadastro from '../../Context/ValidacoesCadastro';
import useErros from '../../Hooks/useEffect'


function TelaUm({aoEnviar}, {email, tel}, {dia, mes, ano} ) {
    const [nome, setNome] = useState("");
    const [nickname, setNickname] = useState("");
    const [checkbox, setCheck] = useState(false);
    const validacoes = useContext(ValidacoesCadastro);
    const [erros, validarCampos, possoEnviar] = useErros(validacoes);
    let linkUm = { TelaUm };
    let linkDois = { TelaDois };

    return (
        <div className="background">
            <Box
                sx={{
                    width: 550,
                    height: 750,
                    backgroundColor: 'white',
                    borderRadius: 5,
                    padding: 5,
                }}
                centered
            >
                <header>
                    <h1 className="title">Team Sign Up</h1>
                    <nav className="navegacao">
                        <ul>
                            <li className="active"><a href={linkUm}>Basic</a></li>
                            <li><a href={linkDois}>Social</a></li>
                            <li>Certificates</li>
                        </ul>
                    </nav>
                </header>
                <form
                    onSubmit={(event) => {
                        event.preventDefault();
                        if (possoEnviar()) {
                            aoEnviar({ nome, nickname, email, tel, dia, mes, ano, idade, checkbox });
                        }
                    }}>
                    <label for="nome" className="text" required>Full Name *</label>
                    <input
                        type="text"
                        id="nome"
                        className="inputMax"
                        required
                        placeholder="Jane Doe"
                        value={nome}
                        onChange={(event) => {
                            setNome(event.target.value);
                        }}
                        onBlur={validarCampos}
                        error={!erros.nome.valido}
                        helperText={erros.nome.texto}
                    />

                    <label for="nickname" className="text">Nickname</label>
                    <input
                        type="text"
                        id="nickname"
                        className="inputMax"
                        placeholder="Mariazinha"
                        value={nickname}
                        onChange={(event) => {
                            setNickname(event.target.value);
                        }}
                    />
                    <EmailTel
                        onBlur={validarCampos}
                        error={!erros.email.valido}
                        helperText={erros.email.texto}
                    />
                    <Birthday
                        onBlur={validarCampos}
                        error={!erros.age.valido}
                        helperText={erros.age.texto}
                    />
                    <label for="age" className="text">Age</label>
                    <input
                        id="age"
                        min="0" max="200"
                        type="number"
                        className="inputBday"
                        disabled
                    />
                    <Checkbox
                        id="checked"
                        sx={{ '& .MuiSvgIcon-root': { fontSize: 28 } }}
                        requerid
                        value={checkbox}
                        onChange={(event) => {
                            setCheck(event.target.value);
                        }}
                        onBlur={validarCampos}
                        error={!erros.checkbox.valido}
                        helperText={erros.checkbox.texto}
                    />
                    <label for="checked" className="textCB">I accept the terms and privacy.</label>
                    <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                        width="medium"
                        sx={{
                            display: 'block',
                            alignBox: 'right',
                        }}
                    >
                        Next
                    </Button>
                </form>
            </Box>
        </div>
    );
}

export default TelaUm;
