function BaseFormulario({ aoEnviar }) {
  return (
    <div className="background">
      <Box
        sx={{
          width: 550,
          height: 750,
          backgroundColor: 'white',
          borderRadius: 5,
          padding: 5,
        }}
        centered
      >
        <TelaUm/>
      </Box>
    </div>
  );
}

export default BaseFormulario;
