function validarTel(tel) {
  if (tel.length < 11) {
    return { valido: false, texto: "Telefone Inv치lido." };
  } else {
    var x = tel.replace(/\D/g, '').match(/(\d{2})(\d{5})(\d{4})/);
    tel = `(${x[1]}) ${x[2]}-${x[3]}`;
    return { valido: true, texto: "" };
  }
}

function validarCheck (checkbox) {
  if (checkbox.checked){
    return { valido: true, texto: "" }
  }else{
    return { valido: false, texto: "Selecione o CheckBox."}
  }
}

function validaIdade({dia,mes,ano}) {
  let birthday = `${dia.value}, ${mes.value - 1}, ${ano.value}`;

  let dataAtual = new Date();
  let aniver = new Date(birthday);
  var idade = dataAtual.getFullYear() - aniver.getFullYear();
  let jaFez = dataAtual.getMonth() - aniver.getMonth();
  document.getElementById("age").value = idade;
  if (jaFez >= 0 && (dataAtual.getDay() >= aniver.getDay()) && idade > 0 && idade < 100) {
    return { valido: true, texto: "" };
  } else if (jaFez < 0 && (dataAtual.getDay() <= aniver.getDay()) && idade > 0 && idade < 100) {
    return { valido: true, texto: "" };
  } else {
    return { valido: false, texto: "Idade Inv치lida." };
  }
}

export { validaIdade, validarTel, validarCheck };
