import React, { useState, useEffect } from 'react';
import '../CSS/TelaStyle.css';
import '../CSS/HeaderStyle.css';
import '../CSS/birthday.css'
import { Button, Checkbox } from '@material-ui/core';
import TelaDois from './TelaDois';
import { validaIdade } from '../JS/cadastro';
/*

*/

function TelaUm({ aoEnviar }) {
    const [nome, setNome] = useState("");
    const [nickname, setNickname] = useState("");
    const [email, setEmail] = useState("");
    const [tel, setTel] = useState("");
    const [dia, setDia] = useState("");
    const [mes, setMes] = useState("");
    const [ano, setAno] = useState("");
    const [aniver, setAniver] = useState({
        dia: dia,
        mes: mes,
        ano: ano,
        idade: '',
    });

    const [checkbox, setCheck] = useState(false);

    const [dados, setDados] = useState({
        nome: '',
        nickname: '',
        email: '',
        tel: '',
        aniver: {
            dia: 0,
            mes: 0,
            ano: 0,
        },
        checkbox: false,
    })

    const setIdade = () => {
        const idade = validaIdade(dados.dia, dados.mes, dados.ano);
        console.log(idade);
        setAniver({
            ...aniver,
            idade: idade,
        });
    }


    useEffect(() => {
        if (localStorage.getItem('fullName') !== null) {
            let aniver = {
                dia: localStorage.getItem('day'),
                mes: localStorage.getItem('month'),
                ano: localStorage.getItem('year'),
            }

            
        setDados({
            ...dados,
            nome: localStorage.getItem('fullName'),
            nickname: localStorage.getItem('nickName'),
            email: localStorage.getItem('email'),
            tel: localStorage.getItem('phone'),
            aniver,
            checkbox: localStorage.getItem('checkebox'),
          })
        }
      }, [])

let linkUm = { TelaUm };
let linkDois = { TelaDois };

return (
    <>
        <header>
            <h1 className="title">Team Sign Up</h1>
            <nav className="navegacao">
                <ul>
                    <li className="active"><a href={linkUm}>Basic</a></li>
                    <li><a href={linkDois}>Social</a></li>
                    <li>Certificates</li>
                </ul>
            </nav>
        </header>
        <form
            onSubmit={(event) => {
                event.preventDefault();
                aoEnviar();
                localStorage.setItem('nome', setNome);
                localStorage.setItem('nickname', setNickname);
                localStorage.setItem('tel', setTel);
                localStorage.setItem('email', setEmail);
                localStorage.setItem('dia', setDia);
                localStorage.setItem('mes', setMes);
                localStorage.setItem('ano', setAno);

            }}>
            <label for="nome" className="text" required>Full Name *</label>
            <input
                type="text"
                id="nome"
                className="inputMax"
                required
                placeholder="Jane Doe"
                value={nome}

                onChange={(event) => {
                    setNome(event.target.value);
                }}
                name="nome"
            />

            <label for="nickname" className="text">Nickname</label>
            <input
                type="text"
                id="nickname"
                className="inputMax"
                placeholder="Mariazinha"
                value={nickname}
                name="nickname"
                onChange={(event) => {
                    setNickname(event.target.value);
                }}
            />
            <div className="boxEmailTel box-input">
                <div className="boxEmail">
                    <label for="inputEmail" className="text">Email *</label>
                    <input
                        id="inputEmail"
                        type="email"
                        className="inputShort"
                        placeholder="mariazinha@email.com"
                        required
                        value={email}
                        name="email"
                        onChange={(event) => {
                            setEmail(event.target.value);
                        }}
                    />
                </div>
                <div className="boxTel">
                    <label for="telefone" className="text align">Phone</label>
                    <input
                        id="telefone"
                        type="tel"
                        pattern="\([0-9]{2}\) [0-9]{5}-[0-9]{4}$"
                        placeholder="(00) 00000-0000"
                        className="inputShort"
                        name="tel"
                        value={tel}
                        onChange={(event) => {
                            setTel(event.target.value.replace(/\D/g, '').match(/(d{2})(\d{5})(\d{4})/));
                        }}
                    />
                </div>
            </div>
            <div className="box-Bday box-input">
                <h3 className="pbirth">Birthday *</h3>
                <div className="boxdia">
                    <label className="text" for="dia">Day</label>
                    <input
                        id="dia"
                        min="01" max="31"
                        type="number"
                        pattern="[0-9]{2}"
                        placeholder="01"
                        className="inputBday"
                        required
                        value={dia}
                        name="dia"
                        onChange={(event) => {
                            setDia(event.target.value);
                            console.log(setDia);
                        }}
                    />
                </div>
                <div className="boxmes">
                    <label for="mes" className="text">Month</label>
                    <input
                        id="mes"
                        min="01" max="12"
                        type="number"
                        pattern="[0-9]{2}"
                        placeholder="01"
                        className="inputBday"
                        required
                        value={mes}
                        name="mes"
                        onChange={(event) => {
                            setMes(event.target.value);
                        }}
                    />
                </div>
                <div className="boxano">
                    <label for="ano" className="text">Year</label>
                    <input
                        id="ano"
                        min="1800" max="2021"
                        type="number"
                        pattern="[0-9]{4}"
                        placeholder="1800"
                        className="inputBday"
                        required
                        value={ano}
                        name="ano"
                        onChange={(event) => {
                            setAno(event.target.value);
                        }}
                    />
                </div>
                <div className="boxage">
                    <label for="age" className="text">Age</label>
                    <input
                        id="age"
                        min="0" max="200"
                        type="number"
                        className="inputBday"
                        disabled
                        name="idade"
                        setIdade={setIdade}
                    />
                </div>
            </div>
            <Checkbox
                id="checked"
                sx={{
                    marginRight: 3,
                    '& .MuiSvgIcon-root': { fontSize: 28 }
                }}
                required
                value={checkbox}
                onChange={(event) => {
                    setCheck(event.target.value);
                }}
                name="checkbox"

            />
            <label for="checked" className="textCB">I accept the terms and privacy.</label>
            <Button
                type="submit"
                variant="contained"
                color="primary"
                width="sm"
                sx={{
                    display: 'block',
                    marginTop: 4,
                    marginLeft: 55,
                    '& .MuiSvgIcon-NavigateNext': { fontSize: 5 }
                }}
            >
                Next
            </Button>
        </form>
    </>
);
}

export default TelaUm;
