import React, { useContext, useState } from 'react';
import '../CSS/TelaStyle.css';
import '../CSS/HeaderStyle.css';
import '../CSS/birthday.css'
import { Button, Checkbox, Box } from '@material-ui/core';
import TelaDois from './TelaDois';
import ValidacoesCadastro from '../../Context/ValidacoesCadastro';
import useErros from '../../Hooks/useErros';


function TelaUm({ aoEnviar }) {
    const [nome, setNome] = useState("");
    const [nickname, setNickname] = useState("");
    const [email, setEmail] = useState("");
    const [tel, setTel] = useState("");
    const [dia, setDia] = useState("");
    const [mes, setMes] = useState("");
    const [ano, setAno] = useState("");
    //const [checkbox, setCheck] = useState(false);
    const validacoes = useContext(ValidacoesCadastro);
    const [erros, validarCampos, possoEnviar] = useErros(validacoes);
    let linkUm = { TelaUm };
    let linkDois = { TelaDois };

    return (
        <div className="background">
            <Box
                sx={{
                    width: 550,
                    height: 750,
                    backgroundColor: 'white',
                    borderRadius: 5,
                    padding: 5,
                }}
                centered
            >
                <header>
                    <h1 className="title">Team Sign Up</h1>
                    <nav className="navegacao">
                        <ul>
                            <li className="active"><a href={linkUm}>Basic</a></li>
                            <li><a href={linkDois}>Social</a></li>
                            <li>Certificates</li>
                        </ul>
                    </nav>
                </header>
                <form
                    onSubmit={(event) => {
                        event.preventDefault();
                        if (possoEnviar()) {
                            aoEnviar({ nome, nickname, email, tel, dia, mes, ano });
                        }
                    }}>
                    <label for="nome" className="text" required>Full Name *</label>
                    <input
                        type="text"
                        id="nome"
                        className="inputMax"
                        required
                        placeholder="Jane Doe"
                        value={nome}
                        onChange={(event) => {
                            setNome(event.target.value);
                        }}
                        onBlur={validarCampos}
                        error={!erros.nome.valido}
                        helperText={erros.nome.texto}
                    />

                    <label for="nickname" className="text">Nickname</label>
                    <input
                        type="text"
                        id="nickname"
                        className="inputMax"
                        placeholder="Mariazinha"
                        value={nickname}
                        onChange={(event) => {
                            setNickname(event.target.value);
                        }}
                    />
                    <div className="boxEmailTel box-input">
                        <label for="inputEmail" className="text">Email *</label>
                        <input
                            id="inputEmail"
                            type="email"
                            className="inputShort"
                            placeholder="mariazinha@email.com"
                            required
                            value={email}
                            onChange={(event) => {
                                setEmail(event.target.value);
                            }}
                            onBlur={validarCampos}
                            error={!erros.email.valido}
                            helperText={erros.email.texto}
                        />
                        <label for="telefone" className="text">Phone</label>
                        <input
                            id="telefone"
                            type="tel"
                            pattern="\([0-9]{2}\) [0-9]{5}-[0-9]{4}$"
                            placeholder="(00) 00000-0000"
                            className="inputShort"
                            value={tel}
                            onChange={(event)=> {
                                setTel(event.target.value);
                            }}
                            onBlur={validarCampos}
                            error={!erros.tel.valido}
                            helperText={erros.tel.texto}
                        />
                    </div>
                    <div >
                        <h3 className="pbirth">Birthday *</h3>
                        <label className="text" for="dia">Day</label>
                        <input
                            id="dia"
                            min="01" max="31"
                            type="number"
                            pattern="[0-9]{2}"
                            placeholder="01"
                            className="inputBday"
                            required
                            value={dia}
                            onChange={(event) => {
                                setDia(event.target.value);
                            }}
                        />
                        <label for="mes" className="text">Month</label>
                        <input
                            id="mes"
                            min="01" max="12"
                            type="number"
                            pattern="[0-9]{2}"
                            placeholder="01"
                            className="inputBday"
                            required
                            value={mes}
                            onChange={(event) => {
                                setMes(event.target.value);
                            }}
                        />
                        <label for="ano" className="text">Year</label>
                        <input
                            id="ano"
                            min="1800" max="2021"
                            type="number"
                            pattern="[0-9]{4}"
                            placeholder="1800"
                            className="inputBday"
                            required
                            value={ano}
                            onChange={(event) => {
                                setAno(event.target.value);
                            }}
                            onBlur={validarCampos}
                            error={!erros.ano.valido}
                            helperText={erros.ano.texto}
                        />

                        <label for="age" className="text">Age</label>
                        <input
                            id="age"
                            min="0" max="200"
                            type="number"
                            className="inputBday"
                            disabled
                            onBlur={validarCampos}
                        />
                    </div>
                    <Checkbox
                        id="checked"
                        sx={{ '& .MuiSvgIcon-root': { fontSize: 28 } }}
                        requerid

                    />
                    <label for="checked" className="textCB">I accept the terms and privacy.</label>
                    <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                        width="sm"
                        sx={{
                            display: 'block',
                            alignBox: 'right',
                        }}
                    >
                        Next
                        <img src="/icons/chevron-right.svg" alt="chevron-right" />
                    </Button>
                </form>
            </Box>
        </div>
    );
}

export default TelaUm;

