import React from 'react';
import '../Pages/Css/TelaStyle.css';
/*import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';*/

function idade() {
    let inputAno = document.getElementById("year").addEventListener('input', function(){
        let day = document.getElementById ("dia");
        let month    
    }); 

    function calculaIdade(birthday) {
        let dataAtual = new Date();
        let aniver = new Date(birthday);
        var idade = dataAtual.getFullYear() - aniver.getFullYear();
        let jaFez = dataAtual.getMonth() - aniver.getMonth();
        if (jaFez >= 0) {
            return idade;
        } else {
            return (idade - 1);
        }
    }
}

function Birthday() {
    return (
        <div>
            <h3>Birthday *</h3>
            <label class="text" for="dia">Day</label>
            <input
                id="dia"
                min="01" max="31"
                type="number"
                pattern="[0-9]{2}"
                placeholder="01"
                class="inputBday"
                required
            />
            <label for="mes" class="text">Month</label>
            <input
                id="mes"
                min="01" max="12"
                type="number"
                pattern="[0-9]{2}"
                placeholder="01"
                class="inputBday"
                required
            />
            <label for="ano" class="text">Year</label>
            <input
                id="ano"
                min="1800" max="2021"
                type="number"
                pattern="[0-9]{4}"
                placeholder="1800"
                class="inputBday"
                required
            />
            <label for="age" class="text" onChange={Age}>Age</label>
            <input
                id="age"
                min="0" max="200"
                type="number"
                class="inputBday"
                disabled
            />
        </div>
    );
}

export default Birthday;


