import React, { useEffect, useState } from 'react';
import TelaUm from './TelaUm';
import TelaTres from './TelaTres';
import TelaDois from './TelaDois';
import '../CSS/TelaStyle.css';
import { Stepper, Step, StepLabel } from "@material-ui/core";

function BaseFormulario({ aoEnviar }) {
  const [etapaAtual, setEtapaAtual] = useState(0);
  const [dadosColetados, setDados] = useState({});

  useEffect(() => {
    if (etapaAtual === formularios.length - 1) {
      aoEnviar(dadosColetados);
    }
  });

  const formularios = [
    <TelaUm aoEnviar={coletarDados} />,
    <TelaDois aoEnviar={coletarDados} />,
    <TelaTres aoEnviar={coletarDados} />,
    //<TelaQuatro />,
  ];

  function coletarDados(dados) {
    setDados({ ...dadosColetados, ...dados });
    proximo();
  }
  function proximo() {
    setEtapaAtual(etapaAtual + 1);
  }
  return (
    <>
      <header>
        <h1 className="title">Team Sign Up</h1>
        <nav active={etapaAtual} className="navegacao">
          <ul>
            <li><a href={TelaUm}>Basic</a></li>
            <li><a href={TelaDois}>Social</a></li>
            <li><a href={TelaTres}>Certificates</a></li>
          </ul>
        </nav>
      </header>
      {formularios[etapaAtual]}
    </>
  );
}

export default BaseFormulario;
