import React, { useState, useContext } from 'react';
import { Button } from '@material-ui/core';
import TelaUm from './TelaUm';
import '../CSS/TelaStyle.css';
import useErros from '../../Hooks/useErros';
import ValidacoesCadastro from '../../Context/ValidacoesCadastro';

function TelaDois({ aoEnviar }) {
    const [Linkedin, setLinkedin] = useState("");
    const [Github, setGithub] = useState("");
    const validacoes = useContext(ValidacoesCadastro);
    const [erros, validarCampos, possoEnviar] = useErros(validacoes);

    let linkUm = { TelaUm };
    let linkDois = { TelaDois };
    return (
        <>
            <header>
                <h1 className="title">Team Sign Up</h1>
                <nav className="navegacao">
                    <ul>
                        <li ><a href={linkUm}>Basic</a></li>
                        <li className="active"><a href={linkDois}>Social</a></li>
                        <li>Certificates</li>
                    </ul>
                </nav>
            </header>
            <form
                onSubmit={(event) => {
                    event.preventDefault();
                    if (possoEnviar()) {
                        aoEnviar({ Linkedin, Github });
                    }
                }}>
                <label for="linkedin" className="text">Linkedin</label>
                <input
                    type="text"
                    id="linkedin"
                    className="inputMax"
                    placeholder="https://www.linkedin.com/in/foo-bar-3a0560104/"
                    value={Linkedin}
                    name={Linkedin}
                    onChange={(event) => {
                        setLinkedin(event.target.value);
                    }}
                    onBlur={validarCampos}
                    error={!erros.Linkedin.valido}
                    helperText={erros.Linkedin.texto}
                />
                <label for="github" className="text">Github *</label>
                <input
                    type="text"
                    id="github"
                    className="inputMax"
                    required
                    placeholder="https://github.com/foobar"
                    value={Github}
                    name={Github}
                    onChange={(event) => {
                        setGithub(event.target.value);
                    }}
                    onBlur={validarCampos}
                    error={!erros.Github.valido}
                    helperText={erros.Github.texto}
                />
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    width="sm"
                    sx={{
                        display: 'block',
                        marginTop: 4,
                        marginLeft: 55,
                    }}
                >
                    Next
                </Button>
            </form>
        </>
    );
}
export default TelaDois;
