import React from 'react';
import './Css/TelaStyle.css';
import '../Components/CSS/HeaderStyle.css'
import { Button, Checkbox, Box } from '@material-ui/core';
//import TelaDois from './TelaDois';
import EmailTel from '../Components/EmailTel';
import Birthday from '../Components/Birthday';
import validaLink from '../Js/ValidaLink.js';

function TelaUm() {
    let linkUm = { TelaUm };
    let linkDois = function () {
        let nome = document.getElementById("nome");
        let Email = document.getElementById("inputEmail");
        let Day = document.getElementById("dia");
        let Month = document.getElementById("mes");
        let Year = document.getElementById("ano");
        let CheckBox = document.getElementById("checked");

        let linkDois = '../Pages/TelaDois.jsx';
        if (nome.value.lenght > 0 && Email.value.lenght > 0 && Day.value.lenght > 0 && Month.value.lenght > 0 && Year.value.lenght > 0) {
            if (CheckBox.checked) {
                return linkDois;
            }
        }
        return;
    };

    return (
        <div class="background">
            <Box
                sx={{
                    width: 550,
                    height: 750,
                    backgroundColor: 'white',
                    borderRadius: 5,
                    padding: 5,
                }}
                centered
            >
                <header>
                    <h1 class="title">Team Sign Up</h1>
                    <nav class="navegacao">
                        <ul>
                            <li class="active"><a href={linkUm}>Basic</a></li>
                            <li><a href={linkDois}>Social</a></li>
                            <li>Certificates</li>
                        </ul>
                    </nav>
                </header>
                <form action="TelaDois.jsx">
                    <label for="nome" class="text">Full Name *</label>
                    <input type="text" id="nome" class="inputMax" required placeholder="Jane Doe" />
                    <label for="nickname" class="text">Nickname</label>
                    <input type="text" id="nickname" class="inputMax" placeholder="Mariazinha" />
                    <EmailTel />
                    <Birthday />
                    <Checkbox id="checked" label="I accept the terms and privacy." sx={{ '& .MuiSvgIcon-root': { fontSize: 28 } }} />
                    <Button type="submit" variant="contained" color="primary" width="sm">
                        Next
                    </Button>
                </form>
            </Box>
        </div>
    );
}

export default TelaUm;
