import React, { useEffect, useState, useContext } from 'react';
import '../CSS/TelaStyle.css';
import { Button } from '@material-ui/core';
import TelaUm from './TelaUm';
import TelaDois from './TelaDois';
import AlertCertificates from '../AlertCertificates';
import ValidacoesCadastro from '../../Context/ValidacoesCadastro';
import useErros from '../../Hooks/useErros';



function TelaTres({ aoEnviar }) {
    let Certificates = 0;
    let linkUm = TelaUm;
    let linkDois = TelaDois;
    const [teamName, setTeamName] = useState("");
    const [institution, setInstitution] = useState("");
    const [graduation, setGraduation] = useState("");

    useEffect(() => {
        if (Certificates >= 5) {
            < AlertCertificates />
        }
    });

    return (
        <>

            <form
                onSubmit={(event) => {
                    event.preventDefault();
                    if (possoEnviar()) {
                        aoEnviar({ teamName, institution, graduation });
                    }
                }}
            >
                <header>
                    <h1 className="title">Team Sign Up</h1>
                    <nav className="navegacao">
                        <ul>
                            <li ><a href={linkUm}>Basic</a></li>
                            <li><a href={linkDois}>Social</a></li>
                            <li className="active">Certificates</li>
                        </ul>
                    </nav>
                </header>
                <div className="box-input">
                    <p className="text">Certificates</p>
                    <div className="input-style box-certificate-input">
                        <input
                            id="certificates"
                            type="text"
                            className="certificates-input"
                            placeholder="http://www.linkedin.com/in/foo-bar-3a0560104/"
                        />

                    </div>
                    <div className="buttons">
                        <div className="dropdown"></div>
                        <div
                            className="more"
                            onChange={(event) => {
                                Certificates = event.target.length;
                            }}
                        >
                            <i data-feather="plus" className="plus"></i>
                            <Button
                                type="submit"
                                variant="contained"
                                color="primary"
                                width="medium"
                                sx={{
                                    display: 'block',
                                    marginBottom: 1,
                                    marginLeft: 55,
                                }}
                            >
                                More
                            </Button>
                            <i data-feather="chevron-right" className="chevron-right"></i>
                        </div>
                    </div>
                </div>
                <label for="teamName" className="labelText text">Team Name *</label>
                <input
                    type="text"
                    id="teamName"
                    className="inputMax"
                    required
                    placeholder="https://www.linkedin.com/in/foo-bar-3a0560104/"
                    value={teamName}
                    name={teamName}
                    onChange={(event) => {
                        setTeamName(event.target.value);
                    }}
                    onBlur={validarCampos}
                    error={!erros.teamName.valido}
                    helperText={erros.teamName.texto}
                />

                <label for="institution" className="labelText text">Institution *</label>
                <input
                    type="text"
                    id="institution"
                    className="inputMax"
                    required
                    placeholder="Universidade Federal da Paraíba"
                    value={institution}
                    name={institution}
                    onChange={(event) => {
                        setInstitution(event.target.value);
                    }}
                    onBlur={validarCampos}
                    error={!erros.institution.valido}
                    helperText={erros.institution.texto}
                />

                <label for="graduation" className="labelText text">Graduation *</label>
                <input
                    type="text"
                    id="graduation"
                    className="inputMax"
                    required
                    placeholder="Ciências da Computação"
                    value={graduation}
                    name={graduation}
                    onChange={(event) => {
                        setGraduation(event.target.value);
                    }}
                    onBlur={validarCampos}
                    error={!erros.graduation.valido}
                    helperText={erros.graduation.texto}
                />

                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    width="medium"
                    sx={{
                        display: 'block',
                        marginTop: 4,
                        marginLeft: 55,
                    }}
                >
                    Finish
                </Button>
            </form>
        </>
    );
}

export default TelaTres;
